query.sql:
select v.*,(append_amount-emerge_amount) end_amount from (
select account_id, account_code, account_name, (select ifnull(sum(amount),0) from exp_account_buget m where m.account_id = t.account_id and m.period_id = ${@period_id} and m.book_id = ${@book_id}) append_amount, (select ifnull(sum(ifnull(dr_amount,0)-ifnull(cr_amount,0)),0) from exp_item i,exp_item_line l where l.account_id = t.account_id and i.item_id = l.item_id and i.period_id = ${@period_id} and i.book_id = ${@book_id}) emerge_amount from exp_account t where t.account_type = '0' and t.book_id = ${@book_id} #AND_CLAUSE# order by t.priority
) v


  select t.*,
  		(select user_name from sys_user u where u.user_id = t.add_user_id) add_user
	from exp_account_buget t
   where t.period_id = ${@period_id}
     and t.account_id = ${@account_id}
	 and t.book_id = ${@book_id}
	order by t.add_time

sqls.executeSql:

delete from exp_account_buget
where book_id = ${@book_id}
  and period_id = ${@period_id}
